
/* Se obtiene LIMPIANDO los archivos del DRIVE, ver LEEME */

DROP TABLE IF EXISTS artist;

/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE artist (
  id int NOT NULL AUTO_INCREMENT,
  name varchar(100) COLLATE utf8mb4_general_ci NOT NULL,
  knownas varchar(100) COLLATE utf8mb4_general_ci NOT NULL,
  dates varchar(100) COLLATE utf8mb4_general_ci NOT NULL,
  slug varchar(100) COLLATE utf8mb4_general_ci DEFAULT NULL,
  bio text COLLATE utf8mb4_general_ci NOT NULL,
  PRIMARY KEY (id)
) ENGINE=InnoDB AUTO_INCREMENT=193 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

CREATE UNIQUE INDEX artist_slug ON artist (slug);


